css:
.watermark {
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    opacity: 0.1; /* Adjust opacity to your preference */
    z-index: 1000; /* Keeps the watermark on top of other content */
    pointer-events: none; /* Ensures the watermark doesn't interfere with interactions */
    white-space: nowrap;
    width: 80%; /* Adjust width as needed */
}

.watermark-footer {
    position: fixed;
    bottom: 10px; /* Adjust this value to move it higher or lower in the footer */
    left: 50%;
    transform: translateX(-50%);
    opacity: 0.7; /* Adjust opacity to your preference */
    z-index: 1000; /* Keeps the watermark on top of other content */
    pointer-events: none; /* Ensures the watermark doesn't interfere with interactions */
    width: 80%; /* Adjust width as needed */
}

